package org.example.day6;

import java.util.*;

public class Day6 {
    private int marker;

    public int getMarker() {
        String input = "";
        int messageSize = 14;

        for (int i = 0; i < input.length() - messageSize - 1; i++) {
            String letters = input.substring(i, i + messageSize);

            Set<Character> checkedCharacters = new HashSet<>();

            for (char c : letters.toCharArray()) {
                checkedCharacters.add(c);
            }
            if (checkedCharacters.size() == messageSize) {
                marker = i + messageSize;
                break;
            }
        }

        return marker;
    }
}
